import React, { useEffect, useState } from "react";

const ImgSlider = ({ slides }) => {
  
  const [currentIndex, setCurrentIndex] = useState(0);

  const goToPrevious = () => {
    const isFirstSlide = currentIndex === 0;
    const newIndex = isFirstSlide ? slides.length - 1 : currentIndex - 1;
    setCurrentIndex(newIndex);
  };
  const goToNext = () => {
    const isLastSlide = currentIndex === slides.length - 1;
    const newIndex = isLastSlide ? 0 : currentIndex + 1;
    setCurrentIndex(newIndex);
  };
  const goToSlide = (slideIndex) => {
    setCurrentIndex(slideIndex);
  };

  useEffect(() => {
    let timer1 = setTimeout(() => {goToNext();}, 5000);
    return ()=> {
      clearTimeout(timer1)
    }
  }, [currentIndex]);

  

  return (
    <div className="max-w-[9000px] p-3 m-4 h-full " >
      <div className=" transform translate-y-1 ml-2  cursor-pointer"  onClick={goToPrevious}>
        {" "}
        ❰{" "}
      </div>
      <div  className=" transform translate-y-1 w-2 mr-2 p-2 cursor-pointer" onClick={goToNext}>
        {" "}
        ❱{" "}
      </div>
      <div  style={{ backgroundImage: `url(${slides[currentIndex].url})` }}
        className="border-2 justify-center bg-cover"
      ></div>
      <div  className=" flex justify-center">
        {slides.map((slide, slideIndex) => (
          <div
            key={slideIndex}
            className=" cursor-pointer mx-3 text-xl "
            onClick={() => goToSlide(slideIndex)}
          >
            ●
          </div>
        ))}
      </div>
    </div>
  );
};
export default ImgSlider;
