import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { getCountries, getCountryByName } from "../../redux/actions";
import style from "./NavBar.module.css"
import { FaBars, FaTimes } from "react-icons/fa";
import SearchBar from "../SearchBar/SearchBar";
import {useLocation} from "react-router-dom";
import Filters from "../../components/Filters/Filters";

const NavBar = () => {
    const location = useLocation()
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getCountries())
    },[dispatch])

    const [filter, SetFilter] = useState(false);



    const clickHandler = () => {
        SetFilter(!filter);
    }

    return(
        <div name="navBar "className=" bg-gradient-to-r from-green-800 to-lime-200 justify-around m">
            <Link to = "/home" className={style.btnNav} > Home </Link>
            { location.pathname === "/home" && <SearchBar />}
            <Link to = "/create" className={style.btnNav} > Create Activity </Link>
            {/* Hamburger */}
            <div onClick={() => clickHandler()} className=" z-10">
                {!filter ? <FaBars /> : <FaTimes/> }
            </div>

            {/* Filters */}
            <div className={ !filter ? "hidden" : "absolute top-0 left-0 w-full h-screen bg-lime-700 opacity-80 flex flex-col justify-center items-center"}>
                <Filters />
            </div>
            
            
            
        </div>
    )
}

export default NavBar;
