import { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { getCountries } from "../../redux/actions";
import style from "./NavBar.module.css";
import { FaBars, FaTimes } from "react-icons/fa";
import SearchBar from "../SearchBar/SearchBar";
import { useLocation } from "react-router-dom";
import Filters from "../../components/Filters/Filters";

const NavBar = () => {
  const location = useLocation();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getCountries());
  }, [dispatch]);

  const [filter, SetFilter] = useState(false);

  const clickHandler = () => {
    SetFilter(!filter);
  };

  return (
    <div
      name="navBar "
      className=" bg-gradient-to-r from-green-800 to-lime-200 justify-around flex pt-1 "
    >
      <Link to="/home" className="md:text-2xl text-serif">
        {" "}
        Home{" "}
      </Link>
      {location.pathname === "/home" && <SearchBar />}
      
      {/* Hamburger */}
      <div
        onClick={() => clickHandler()}
        className="xl:bg-green-800 md:bg-green-800 xl:p-4 p-2 z-10 -translate-y-1"
      >
        {!filter ? <FaBars /> : <FaTimes />}
      </div>

      {/* Filters */}
      <div
        className={
          !filter
            ? "hidden"
            : "absolute top-0 left-0 w-9/12 h-screen bg-lime-700 flex flex-col justify-center items-center bg-opacity-80 bg-right"
        }
      >
        <Filters />
      </div>

      <ul
        className={
          !0
            ? "hidden"
            : "absolute top-0 left-0 w-full h-screen bg-[#0a192f] flex  flex-col justify-center items-center"
        }
      >
        <li> <Link to="/create" className="py-6 text-4xl" >
        {" "}
        Create Activity{" "}
      </Link></li>
      </ul>
    </div>
  );
};

export default NavBar;
