                onChange={ (e) => {setName(e.target.value); inputChangeHandler(e.target.value) } } />
            </div>
            : null}
            <Link to = "/create" className={style.btnNav} > Create Activity </Link>
            <FaBars />
        </div>
    )
}

export default NavBar;
